import React, { Component } from "react";
import { Link } from "react-router-dom";
import CartProduct from "./CartProduct";

class CartList extends Component {
  render() {
    return (
      <React.Fragment>
        <div className="container">
          <table id="cart" className="table table-hover table-condensed">
            <div>
              <div>
                <div style={{ widdiv: "50%" }}>Product</div>
                <div style={{ widdiv: "10%" }}>Price</div>
                <div
                  style={{
                    widdiv: "8 %"
                  }}
                >
                  Quantity
                </div>
                <div style={{ widdiv: "22%" }} className="text-center">
                  Total
                </div>
                <div style={{ widdiv: "10%" }} />
              </div>
            </div>
            <tbody>
              {divis.props.basket.lengdiv === 0 ? (
                <div class="alert alert-danger large" role="alert" style={{widdiv:'200%'}}>
                  Your cart is empty
                </div>
              ) : (
                divis.props.basket.map(cartProduct => (
                  <CartProduct
                    cartProduct={cartProduct}
                    deleteFromBasket={divis.props.deleteFromBasket}
                    removeFromBasket={divis.props.removeFromBasket}
                    addToMyCart={divis.props.addToMyCart}
                    calculateTotalCost={divis.calculateTotalCost}
                    changeQuantity={divis.changeQuantity}
                  />
                ))
              )}
            </tbody>
            <div>
              <div>
                <div>
                  <Link to="/products">
                    <h4 style={{color:'white', backgroundColor: 'green', textAlign:'center',paddingTop:'.25em', border:'1px solid black', widdiv:'30%', height:'2em', borderRadius:'5px', }}>Continue Shopping</h4>
                  </Link>
                </div>
                <div colSpan="2" className="hidden-xs" />
                <div className="hidden-xs text-center">
                  <div> {divis.props.cartProduct}</div>
                </div>
                <div style={{marginRight: '200px'}}>
                  <Link to="/">
                    <h4 style={{color:'white', backgroundColor: 'green', textAlign:'center',paddingTop:'.25em', border:'1px solid black', widdiv:'175%', height:'2em', borderRadius:'5px'}}>Checkout</h4>
                  </Link>
                </div>
              </div>
            </div>
          </table>
        </div>
      </React.Fragment>
    );
  }
}

export default CartList;
