import React, { Component } from "react";
import axios from "axios";

class CreatePost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      img_url: "",
      price:"",
      submit: false
    };
  }
  onSubmit = async e => {
    e.preventDefault();
    console.log("state", this.state);
    const {name, price, description, img_url} = this.state
    const data = {
        name,
        price,
        description,
        img_url
    }
    await axios.post(`http://localhost:3000/products`, data)
    .then(res => {
      console.log(res);
    //   this.props.addSportStories(res.data);
      this.setState({
        submit: true
      });

      window.location.reload();
    });
  };

  changeHandler = e => {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  };

  render() {
    return (
      <div id="createPost" className="Home">
        <div className="Story createPost" style={{ height: "90vh"}}>
          <div className="Headline" style={{ backgroundColor: "#b0c4de", width: '50%', margin: '0 auto' }}>
            <p style={{margin: ''}}> CREATE A POST</p>
          </div>
          <div className="form" style={{ height: "70vh"}}>
            <div className="formContent">
              <form className="formStyle">
                {this.state.submit && (
                  <div className="row">
                    <b className="success"> Post Submitted Succefully</b>
                  </div>
                )}
                <div className="row">
                  <label className="label"> Title: </label>
                  <input
                    name="name"
                    type="text"
                    className="input"
                    value={this.state.name}
                    onChange={e => this.changeHandler(e)}
                    required
                  />
                </div>
                <div className="row">
                  <label className="label"> Description: </label>
                  <input
                    name="description"
                    type="text"
                    className="input"
                    value={this.state.description}
                    onChange={e => this.changeHandler(e)}
                    required
                  />
                </div>
                <div className="row">
                  <label className="label"> Image URL: </label>
                  <input
                    name="img_url"
                    type="text"
                    className="input"
                    value={this.state.img_url}
                    onChange={e => this.changeHandler(e)}
                    required
                  />
                </div>

                <div className="row">
                  <label className="label"> Price: </label>
                  <input
                    name="price"
                    type="text"
                    className="input"
                    value={this.state.price}
                    onChange={e => this.changeHandler(e)}
                    required
                  />
                </div>


                <div className="btnrow">
                  <button
                    type="submit"
                    className="btn"
                    onClick={e => this.onSubmit(e)}
                  >
                    Submit Post
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default CreatePost;
